-- Create health_tips table
CREATE TABLE IF NOT EXISTS public.health_tips (
    id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
    title TEXT NOT NULL,
    content TEXT NOT NULL,
    category TEXT DEFAULT 'Geral',
    icon_type TEXT DEFAULT 'Lightbulb',
    created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL
);

-- Enable RLS
ALTER TABLE public.health_tips ENABLE ROW LEVEL SECURITY;

-- Allow authenticated users to read tips
CREATE POLICY "Allow authenticated read health_tips" 
ON public.health_tips FOR SELECT 
TO authenticated 
USING (true);

